"use strict";

var WorkerFarm = require("../../../".concat(parseInt(process.versions.node, 10) < 8 ? 'lib' : 'src', "/WorkerFarm"));

function run() {
  var result = [process.pid];
  return new Promise(function (resolve, reject) {
    WorkerFarm.callMaster({
      location: require.resolve('./master-process-id.js'),
      args: []
    }).then(function (pid) {
      result.push(pid);
      resolve(result);
    })["catch"](reject);
  });
}

function init() {// Do nothing
}

exports.run = run;
exports.init = init;