'use strict';

var path = require('path');

module.exports = function () {
  var isWindows = process.platform === 'win32';
  var yarnPath = isWindows ? path.join('Yarn', 'config', 'global') : path.join('.config', 'yarn', 'global');

  if (__dirname.includes(yarnPath)) {
    return true;
  }

  return false;
};