"use strict";

var xnv = require("xml-name-validator");

var DOMException = require("domexception");

var _require = require("../helpers/namespaces"),
    XML_NS = _require.XML_NS,
    XMLNS_NS = _require.XMLNS_NS; // https://dom.spec.whatwg.org/#validate


exports.name = function (name) {
  var result = xnv.name(name);

  if (!result.success) {
    throw new DOMException("\"" + name + "\" did not match the Name production: " + result.error, "InvalidCharacterError");
  }
};

exports.qname = function (qname) {
  exports.name(qname);
  var result = xnv.qname(qname);

  if (!result.success) {
    throw new DOMException("\"" + qname + "\" did not match the QName production: " + result.error, "InvalidCharacterError");
  }
};

exports.validateAndExtract = function (namespace, qualifiedName) {
  if (namespace === "") {
    namespace = null;
  }

  exports.qname(qualifiedName);
  var prefix = null;
  var localName = qualifiedName;
  var colonIndex = qualifiedName.indexOf(":");

  if (colonIndex !== -1) {
    prefix = qualifiedName.substring(0, colonIndex);
    localName = qualifiedName.substring(colonIndex + 1);
  }

  if (prefix !== null && namespace === null) {
    throw new DOMException("A namespace was given but a prefix was also extracted from the qualifiedName", "NamespaceError");
  }

  if (prefix === "xml" && namespace !== XML_NS) {
    throw new DOMException("A prefix of \"xml\" was given but the namespace was not the XML namespace", "NamespaceError");
  }

  if ((qualifiedName === "xmlns" || prefix === "xmlns") && namespace !== XMLNS_NS) {
    throw new DOMException("A prefix or qualifiedName of \"xmlns\" was given but the namespace was not the XMLNS " + "namespace", "NamespaceError");
  }

  if (namespace === XMLNS_NS && qualifiedName !== "xmlns" && prefix !== "xmlns") {
    throw new DOMException("The XMLNS namespace was given but neither the prefix nor qualifiedName was \"xmlns\"", "NamespaceError");
  }

  return {
    namespace: namespace,
    prefix: prefix,
    localName: localName
  };
};