"use strict";

var t = require('@babel/types');

module.exports = {
  MemberExpression: function MemberExpression(path) {
    // Inline process.browser
    var isProcess = path.node.object.name === 'process';
    var isBrowser = path.node.property.name === 'browser';
    var isAssignment = path.parentPath.type === 'AssignmentExpression';

    if (isProcess && isBrowser) {
      if (isAssignment) {
        path.parentPath.remove();
      } else {
        path.replaceWith(t.booleanLiteral(true));
      }
    }
  }
};